94 #include <android/log.h>
95 #define TIXMLASSERT(x)                                                                              \
96     do                                                                                              \
97     {                                                                                               \
98         if (!(x))                                                                                   \
99         {                                                                                           \
100             __android_log_assert("assert", "grinliz", "ASSERT in '%s' at %d.", __FILE__, __LINE__); \
101         }                                                                                           \
102     } while (false)
103 #else
104 #include <assert.h>
105 #define TIXMLASSERT assert
106 #endif
