144     /*
145         A class that wraps strings. Normally stores the start and end
146         pointers into the XML file itself, and will apply normalization
147         and entity translation if actually read. Can also store (and memory
148         manage) a traditional char[]
149 
150         Isn't clear why TINYXML2_LIB is needed; but seems to fix #719
151     */
152     class TINYXML2_LIB StrPair
153     {
154     public:
155         enum Mode
156         {
157             NEEDS_ENTITY_PROCESSING = 0x01,
158             NEEDS_NEWLINE_NORMALIZATION = 0x02,
159             NEEDS_WHITESPACE_COLLAPSING = 0x04,
160 
161             TEXT_ELEMENT = NEEDS_ENTITY_PROCESSING | NEEDS_NEWLINE_NORMALIZATION,
162             TEXT_ELEMENT_LEAVE_ENTITIES = NEEDS_NEWLINE_NORMALIZATION,
163             ATTRIBUTE_NAME = 0,
164             ATTRIBUTE_VALUE = NEEDS_ENTITY_PROCESSING | NEEDS_NEWLINE_NORMALIZATION,
165             ATTRIBUTE_VALUE_LEAVE_ENTITIES = NEEDS_NEWLINE_NORMALIZATION,
166             COMMENT = NEEDS_NEWLINE_NORMALIZATION
167         };
168 
169         StrPair() : _flags(0), _start(0), _end(0) {}
170         ~StrPair();
171 
172         void Set(char *start, char *end, int flags)
173         {
174             TIXMLASSERT(start);
175             TIXMLASSERT(end);
176             Reset();
177             _start = start;
178             _end = end;
179             _flags = flags | NEEDS_FLUSH;
180         }
181 
182         const char *GetStr();
183 
184         bool Empty() const
185         {
186             return _start == _end;
187         }
188 
189         void SetInternedStr(const char *str)
190         {
191             Reset();
192             _start = const_cast<char *>(str);
193         }
194 
195         void SetStr(const char *str, int flags = 0);
196 
197         char *ParseText(char *in, const char *endTag, int strFlags, int *curLineNumPtr);
198         char *ParseName(char *in);
199 
200         void TransferTo(StrPair *other);
201         void Reset();
202 
203     private:
204         void CollapseWhitespace();
205 
206         enum
207         {
208             NEEDS_FLUSH = 0x100,
209             NEEDS_DELETE = 0x200
210         };
211 
212         int _flags;
213         char *_start;
214         char *_end;
215 
216         StrPair(const StrPair &other);        // not supported
217         void operator=(const StrPair &other); // not supported, use TransferTo()
218     };
