363     /*
364         Parent virtual class of a pool for fast allocation
365         and deallocation of objects.
366     */
367     class MemPool
368     {
369     public:
370         MemPool() {}
371         virtual ~MemPool() {}
372 
373         virtual int ItemSize() const = 0;
374         virtual void *Alloc() = 0;
375         virtual void Free(void *) = 0;
376         virtual void SetTracked() = 0;
377     };
378 
379     /*
380         Template child class to create pools of the correct type.
381     */
382     template <int ITEM_SIZE>
383     class MemPoolT : public MemPool
384     {
385     public:
386         MemPoolT() : _blockPtrs(), _root(0), _currentAllocs(0), _nAllocs(0), _maxAllocs(0), _nUntracked(0) {}
387         ~MemPoolT()
388         {
389             MemPoolT<ITEM_SIZE>::Clear();
390         }
391 
392         void Clear()
393         {
394             // Delete the blocks.
395             while (!_blockPtrs.Empty())
396             {
397                 Block *lastBlock = _blockPtrs.Pop();
398                 delete lastBlock;
399             }
400             _root = 0;
401             _currentAllocs = 0;
402             _nAllocs = 0;
403             _maxAllocs = 0;
404             _nUntracked = 0;
405         }
406 
407         virtual int ItemSize() const
408         {
409             return ITEM_SIZE;
410         }
411         int CurrentAllocs() const
412         {
413             return _currentAllocs;
414         }
415 
416         virtual void *Alloc()
417         {
418             if (!_root)
419             {
420                 // Need a new block.
421                 Block *block = new Block;
422                 _blockPtrs.Push(block);
423 
424                 Item *blockItems = block->items;
425                 for (int i = 0; i < ITEMS_PER_BLOCK - 1; ++i)
426                 {
427                     blockItems[i].next = &(blockItems[i + 1]);
428                 }
429                 blockItems[ITEMS_PER_BLOCK - 1].next = 0;
430                 _root = blockItems;
431             }
432             Item *const result = _root;
433             TIXMLASSERT(result != 0);
434             _root = _root->next;
435 
436             ++_currentAllocs;
437             if (_currentAllocs > _maxAllocs)
438             {
439                 _maxAllocs = _currentAllocs;
440             }
441             ++_nAllocs;
442             ++_nUntracked;
443             return result;
444         }
445 
446         virtual void Free(void *mem)
447         {
448             if (!mem)
449             {
450                 return;
451             }
452             --_currentAllocs;
453             Item *item = static_cast<Item *>(mem);
454 #ifdef TINYXML2_DEBUG
455             memset(item, 0xfe, sizeof(*item));
456 #endif
457             item->next = _root;
458             _root = item;
459         }
460         void Trace(const char *name)
461         {
462             printf("Mempool %s watermark=%d [%dk] current=%d size=%d nAlloc=%d blocks=%d\n",
463                    name, _maxAllocs, _maxAllocs * ITEM_SIZE / 1024, _currentAllocs,
464                    ITEM_SIZE, _nAllocs, _blockPtrs.Size());
465         }
466 
467         void SetTracked()
468         {
469             --_nUntracked;
470         }
471 
472         int Untracked() const
473         {
474             return _nUntracked;
475         }
476 
477         // This number is perf sensitive. 4k seems like a good tradeoff on my machine.
478         // The test file is large, 170k.
479         // Release:     VS2010 gcc(no opt)
480         //      1k:     4000
481         //      2k:     4000
482         //      4k:     3900    21000
483         //      16k:    5200
484         //      32k:    4300
485         //      64k:    4000    21000
486         // Declared public because some compilers do not accept to use ITEMS_PER_BLOCK
487         // in private part if ITEMS_PER_BLOCK is private
488         enum
489         {
490             ITEMS_PER_BLOCK = (4 * 1024) / ITEM_SIZE
491         };
492 
493     private:
494         MemPoolT(const MemPoolT &);       // not supported
495         void operator=(const MemPoolT &); // not supported
496 
497         union Item
498         {
499             Item *next;
500             char itemData[ITEM_SIZE];
501         };
502         struct Block
503         {
504             Item items[ITEMS_PER_BLOCK];
505         };
506         DynArray<Block *, 10> _blockPtrs;
507         Item *_root;
508 
509         int _currentAllocs;
510         int _nAllocs;
511         int _maxAllocs;
512         int _nUntracked;
513     };
