609     /*
610         Utility functionality.
611     */
612     class TINYXML2_LIB XMLUtil
613     {
614     public:
615         static const char *SkipWhiteSpace(const char *p, int *curLineNumPtr)
616         {
617             TIXMLASSERT(p);
618 
619             while (IsWhiteSpace(*p))
620             {
621                 if (curLineNumPtr && *p == '\n')
622                 {
623                     ++(*curLineNumPtr);
624                 }
625                 ++p;
626             }
627             TIXMLASSERT(p);
628             return p;
629         }
630         static char *SkipWhiteSpace(char *const p, int *curLineNumPtr)
631         {
632             return const_cast<char *>(SkipWhiteSpace(const_cast<const char *>(p), curLineNumPtr));
633         }
634 
635         // Anything in the high order range of UTF-8 is assumed to not be whitespace. This isn't
636         // correct, but simple, and usually works.
637         static bool IsWhiteSpace(char p)
638         {
639             return !IsUTF8Continuation(p) && isspace(static_cast<unsigned char>(p));
640         }
641 
642         inline static bool IsNameStartChar(unsigned char ch)
643         {
644             if (ch >= 128)
645             {
646                 // This is a heuristic guess in attempt to not implement Unicode-aware isalpha()
647                 return true;
648             }
649             if (isalpha(ch))
650             {
651                 return true;
652             }
653             return ch == ':' || ch == '_';
654         }
655 
656         inline static bool IsNameChar(unsigned char ch)
657         {
658             return IsNameStartChar(ch) || isdigit(ch) || ch == '.' || ch == '-';
659         }
660 
661         inline static bool IsPrefixHex(const char *p)
662         {
663             p = SkipWhiteSpace(p, 0);
664             return p && *p == '0' && (*(p + 1) == 'x' || *(p + 1) == 'X');
665         }
666 
667         inline static bool StringEqual(const char *p, const char *q, int nChar = INT_MAX)
668         {
669             if (p == q)
670             {
671                 return true;
672             }
673             TIXMLASSERT(p);
674             TIXMLASSERT(q);
675             TIXMLASSERT(nChar >= 0);
676             return strncmp(p, q, nChar) == 0;
677         }
678 
679         inline static bool IsUTF8Continuation(const char p)
680         {
681             return (p & 0x80) != 0;
682         }
683 
684         static const char *ReadBOM(const char *p, bool *hasBOM);
685         // p is the starting location,
686         // the UTF-8 value of the entity will be placed in value, and length filled in.
687         static const char *GetCharacterRef(const char *p, char *value, int *length);
688         static void ConvertUTF32ToUTF8(unsigned long input, char *output, int *length);
689 
690         // converts primitive types to strings
691         static void ToStr(int v, char *buffer, int bufferSize);
692         static void ToStr(unsigned v, char *buffer, int bufferSize);
693         static void ToStr(bool v, char *buffer, int bufferSize);
694         static void ToStr(float v, char *buffer, int bufferSize);
695         static void ToStr(double v, char *buffer, int bufferSize);
696         static void ToStr(int64_t v, char *buffer, int bufferSize);
697         static void ToStr(uint64_t v, char *buffer, int bufferSize);
698 
699         // converts strings to primitive types
700         static bool ToInt(const char *str, int *value);
701         static bool ToUnsigned(const char *str, unsigned *value);
702         static bool ToBool(const char *str, bool *value);
703         static bool ToFloat(const char *str, float *value);
704         static bool ToDouble(const char *str, double *value);
705         static bool ToInt64(const char *str, int64_t *value);
706         static bool ToUnsigned64(const char *str, uint64_t *value);
707         // Changes what is serialized for a boolean value.
708         // Default to "true" and "false". Shouldn't be changed
709         // unless you have a special testing or compatibility need.
710         // Be careful: static, global, & not thread safe.
711         // Be sure to set static const memory as parameters.
712         static void SetBoolSerialization(const char *writeTrue, const char *writeFalse);
713 
714     private:
715         static const char *writeBoolTrue;
716         static const char *writeBoolFalse;
717     };
