2133         // Something of an obvious security hole, once it was discovered.
2134         // Either an ill-formed XML or an excessively deep one can overflow
2135         // the stack. Track stack depth, and error out if needed.
2136         class DepthTracker
2137         {
2138         public:
2139             explicit DepthTracker(XMLDocument *document)
2140             {
2141                 this->_document = document;
2142                 document->PushDepth();
2143             }
2144             ~DepthTracker()
2145             {
2146                 _document->PopDepth();
2147             }
2148 
2149         private:
2150             XMLDocument *_document;
2151         };
