2159     template <class NodeType, int PoolElementSize>
2160     inline NodeType *XMLDocument::CreateUnlinkedNode(MemPoolT<PoolElementSize> &pool)
2161     {
2162         TIXMLASSERT(sizeof(NodeType) == PoolElementSize);
2163         TIXMLASSERT(sizeof(NodeType) == pool.ItemSize());
2164         NodeType *returnNode = new (pool.Alloc()) NodeType(this);
2165         TIXMLASSERT(returnNode);
2166         returnNode->_memPool = &pool;
2167 
2168         _unlinked.Push(returnNode);
2169         return returnNode;
2170     }
